//App.js

import React, { useEffect, useState } from 'react';
import twitterLogo from './assets/twitter-logo.svg';
import './App.css';
import { Connection, PublicKey, clusterApiUrl} from '@solana/web3.js';
import {
  Program, Provider, web3
} from '@project-serum/anchor';

import idl from './idl.json';
import kp from './keypair.json'



require('dotenv').config();


// SystemProgram„ÅØSolana„É©„É≥„Çø„Ç§„É†„Å∏„ÅÆÂèÇÁÖß„Åß„Åô„ÄÇ
const { SystemProgram, Keypair } = web3;

// GIF„Éá„Éº„Çø„Çí‰øùÊåÅ„Åô„Çã„Ç¢„Ç´„Ç¶„É≥„Éà„ÅÆ„Ç≠„Éº„Éö„Ç¢„Çí‰ΩúÊàê„Åó„Åæ„Åô„ÄÇ
const arr = Object.values(kp._keypair.secretKey)
const secret = new Uint8Array(arr)
const baseAccount = web3.Keypair.fromSecretKey(secret)

// IDL„Éï„Ç°„Ç§„É´„Åã„Çâ„Éó„É≠„Ç∞„É©„É†ID„ÇíÂèñÂæó„Åó„Åæ„Åô„ÄÇ
const programID = new PublicKey(idl.metadata.address);

// „Éç„ÉÉ„Éà„ÉØ„Éº„ÇØ„ÇíDevnet„Å´Ë®≠ÂÆö„Åó„Åæ„Åô„ÄÇ
// const network = clusterApiUrl('devnet');

const network = clusterApiUrl(process.env.SOLANA_NETWORK);


// „Éà„É©„É≥„Ç∂„ÇØ„Ç∑„Éß„É≥„ÅåÂÆå‰∫Ü„Åó„Åü„Å®„Åç„Å´ÈÄöÁü•ÊñπÊ≥ï„ÇíÂà∂Âæ°„Åó„Åæ„Åô„ÄÇ
const opts = {
  preflightCommitment: "processed"
}
// ÂÆöÊï∞„ÇíÂÆ£Ë®Ä„Åó„Åæ„Åô„ÄÇ
const TWITTER_HANDLE = '„ÅÇ„Å™„Åü„ÅÆTwitter„Éè„É≥„Éâ„É´';
const TWITTER_LINK = `https://twitter.com/${TWITTER_HANDLE}`;
const TEST_GIFS = [
	'https://media.giphy.com/media/ZqlvCTNHpqrio/giphy.gif',
	'https://media.giphy.com/media/bC9czlgCMtw4cj8RgH/giphy.gif',
	'https://media.giphy.com/media/kC8N6DPOkbqWTxkNTe/giphy.gif',
	'https://media.giphy.com/media/26n6Gx9moCgs1pUuk/giphy.gif'
]

const App = () => {
  // „É¶„Éº„Ç∂„Éº„ÅÆ„Ç¶„Ç©„É¨„ÉÉ„Éà„Ç¢„Éâ„É¨„Çπ„ÅÆstate„ÇíÁÆ°ÁêÜ„Åô„Çã„Åü„ÇÅuseState„Çí‰ΩøÁî®„Åô„Çã„ÄÇ
  const [walletAddress, setWalletAddress] = useState(null);
  const [inputValue, setInputValue] = useState('');
  const [gifList, setGifList] = useState([]);
 /*
  * Phantom Wallet„ÅåÊé•Á∂ö„Åï„Çå„Å¶„ÅÑ„Çã„Åã„Å©„ÅÜ„Åã„ÇíÁ¢∫Ë™ç„Åô„Çã„Åü„ÇÅ„ÅÆÈñ¢Êï∞„Åß„Åô„ÄÇ
  */
  const checkIfWalletIsConnected = async () => {
    try {
      const { solana } = window;

      if (solana) {
        if (solana.isPhantom) {
          console.log('Phantom wallet found!');
          const response = await solana.connect({ onlyIfTrusted: true });
          console.log(
            'Connected with Public Key:',
            response.publicKey.toString()
          );

          /*
           * walletAddress„Å´„É¶„Éº„Ç∂„Éº„ÅÆ„Ç¶„Ç©„É¨„ÉÉ„Éà„Ç¢„Éâ„É¨„Çπ„ÅÆstate„ÇíÊõ¥Êñ∞„Åó„Åæ„Åô„ÄÇ
           */
          setWalletAddress(response.publicKey.toString());
        }
      } else {
        alert('Solana object not found! Get a Phantom Wallet üëª');
      }
    } catch (error) {
      console.error(error);
    }
  };

  const connectWallet = async () => {
    const { solana } = window;

    if (solana) {
      const response = await solana.connect();
      console.log("Connected with Public Key:", response.publicKey.toString());
      setWalletAddress(response.publicKey.toString());
    }
  };

  const onInputChange = (event) => {
    const { value } = event.target;
    setInputValue(value);
  };

  const getProvider = () => {
    const connection = new Connection(network, opts.preflightCommitment);
    const provider = new Provider(
      connection, window.solana, opts.preflightCommitment,
    );
    return provider;
  }

  const createGifAccount = async () => {
    try {
      const provider = getProvider();
      const program = new Program(idl, programID, provider);
      console.log("ping")
      await program.rpc.startStuffOff({
        accounts: {
          baseAccount: baseAccount.publicKey,
          user: provider.wallet.publicKey,
          systemProgram: SystemProgram.programId,
        },
        signers: [baseAccount]
      });
      console.log("Created a new BaseAccount w/ address:", baseAccount.publicKey.toString())
      await getGifList();

    } catch(error) {
      console.log("Error creating BaseAccount account:", error)
    }
  }

  const sendGif = async () => {
    if (inputValue.length === 0) {
      console.log("No gif link given!")
      return
    }
    setInputValue('');
    console.log('Gif link:', inputValue);
    try {
      const provider = getProvider();
      const program = new Program(idl, programID, provider);

      await program.rpc.addGif(inputValue, {
        accounts: {
          baseAccount: baseAccount.publicKey,
          user: provider.wallet.publicKey,
        },
      });
      console.log("GIF successfully sent to program", inputValue)

      await getGifList();
    } catch (error) {
      console.log("Error sending GIF:", error)
    }
  };

  const renderNotConnectedContainer = () => (
    <button
      className="cta-button connect-wallet-button"
      onClick={connectWallet}
    >
      Connect to Wallet
    </button>
  );

  const renderConnectedContainer = () => {
    // „Éó„É≠„Ç∞„É©„É†„Ç¢„Ç´„Ç¶„É≥„Éà„ÅåÂàùÊúüÂåñ„Åï„Çå„Å¶„ÅÑ„Çã„Åã„Å©„ÅÜ„Åã„ÉÅ„Çß„ÉÉ„ÇØ„Åó„Åæ„Åô„ÄÇ
      if (gifList === null) {
        return (
          <div className="connected-container">
            <button className="cta-button submit-gif-button" onClick={createGifAccount}>
              Do One-Time Initialization For GIF Program Account
            </button>
          </div>
        )
      }
      // „Ç¢„Ç´„Ç¶„É≥„Éà„ÅåÂ≠òÂú®„Åó„ÅüÂ†¥Âêà„ÄÅ„É¶„Éº„Ç∂„Éº„ÅØGIF„ÇíÊäïÁ®ø„Åô„Çã„Åì„Å®„Åå„Åß„Åç„Åæ„Åô„ÄÇ
      else {
        return(
          <div className="connected-container">
            <form
              onSubmit={(event) => {
                event.preventDefault();
                sendGif();
              }}
            >
              <input
                type="text"
                placeholder="Enter gif link!"
                value={inputValue}
                onChange={onInputChange}
              />
              <button type="submit" className="cta-button submit-gif-button">
                Submit
              </button>
            </form>
            <div className="gif-grid">
              {/* index„Çí„Ç≠„Éº„Å®„Åó„Å¶‰ΩøÁî®„Åó„ÄÅGIF„Ç§„É°„Éº„Ç∏„Å®„Åó„Å¶item.gifLink„Å´Â§âÊõ¥„Åó„Åæ„Åó„Åü„ÄÇ */}
              {gifList.map((item, index) => (
                <div className="gif-item" key={index}>
                  <img src={item.gifLink} />
                </div>
              ))}
            </div>
          </div>
        )
      }
    }



  /*
   * ÂàùÂõû„ÅÆ„É¨„É≥„ÉÄ„É™„É≥„Ç∞ÊôÇ„Å´„ÅÆ„Åø„ÄÅPhantom Wallet„ÅåÊé•Á∂ö„Åï„Çå„Å¶„ÅÑ„Çã„Åã„Å©„ÅÜ„ÅãÁ¢∫Ë™ç„Åó„Åæ„Åô„ÄÇ
   */
  useEffect(() => {
    const onLoad = async () => {
      await checkIfWalletIsConnected();
    };
    window.addEventListener('load', onLoad);
    return () => window.removeEventListener('load', onLoad);
  }, []);

  const getGifList = async() => {
    try {
      const provider = getProvider();
      const program = new Program(idl, programID, provider);
      const account = await program.account.baseAccount.fetch(baseAccount.publicKey);

      console.log("Got the account", account)
      setGifList(account.gifList)

    } catch (error) {
      console.log("Error in getGifList: ", error)
      setGifList(null);
    }
  }

  useEffect(() => {
    if (walletAddress) {
      console.log('Fetching GIF list...');
      getGifList()
    }
  }, [walletAddress]);


  return (
    <div className="App">
      <div className="container">
        <div className="header-container">
          <p className="header">üñº GIF Portal</p>
          <p className="sub-text">
            View your GIF collection ‚ú®
          </p>
          {!walletAddress && renderNotConnectedContainer()}
        </div>
