//App.js

import React, { useEffect, useState } from 'react';
import twitterLogo from './assets/twitter-logo.svg';
import './App.css';

// ÂÆöÊï∞„ÇíÂÆ£Ë®Ä„Åó„Åæ„Åô„ÄÇ
const TWITTER_HANDLE = '„ÅÇ„Å™„Åü„ÅÆTwitter„Éè„É≥„Éâ„É´';
const TWITTER_LINK = `https://twitter.com/${TWITTER_HANDLE}`;
const TEST_GIFS = [
	'https://media.giphy.com/media/ZqlvCTNHpqrio/giphy.gif',
	'https://media.giphy.com/media/bC9czlgCMtw4cj8RgH/giphy.gif',
	'https://media.giphy.com/media/kC8N6DPOkbqWTxkNTe/giphy.gif',
	'https://media.giphy.com/media/26n6Gx9moCgs1pUuk/giphy.gif'
]

const App = () => {
  // „É¶„Éº„Ç∂„Éº„ÅÆ„Ç¶„Ç©„É¨„ÉÉ„Éà„Ç¢„Éâ„É¨„Çπ„ÅÆstate„ÇíÁÆ°ÁêÜ„Åô„Çã„Åü„ÇÅuseState„Çí‰ΩøÁî®„Åô„Çã„ÄÇ
  const [walletAddress, setWalletAddress] = useState(null);
  const [inputValue, setInputValue] = useState('');
 /*
  * Phantom Wallet„ÅåÊé•Á∂ö„Åï„Çå„Å¶„ÅÑ„Çã„Åã„Å©„ÅÜ„Åã„ÇíÁ¢∫Ë™ç„Åô„Çã„Åü„ÇÅ„ÅÆÈñ¢Êï∞„Åß„Åô„ÄÇ
  */
  const checkIfWalletIsConnected = async () => {
    try {
      const { solana } = window;

      if (solana) {
        if (solana.isPhantom) {
          console.log('Phantom wallet found!');
          const response = await solana.connect({ onlyIfTrusted: true });
          console.log(
            'Connected with Public Key:',
            response.publicKey.toString()
          );

          /*
           * walletAddress„Å´„É¶„Éº„Ç∂„Éº„ÅÆ„Ç¶„Ç©„É¨„ÉÉ„Éà„Ç¢„Éâ„É¨„Çπ„ÅÆstate„ÇíÊõ¥Êñ∞„Åó„Åæ„Åô„ÄÇ
           */
          setWalletAddress(response.publicKey.toString());
        }
      } else {
        alert('Solana object not found! Get a Phantom Wallet üëª');
      }
    } catch (error) {
      console.error(error);
    }
  };

  const connectWallet = async () => {
    const { solana } = window;

    if (solana) {
      const response = await solana.connect();
      console.log("Connected with Public Key:", response.publicKey.toString());
      setWalletAddress(response.publicKey.toString());
    }
  };

  const onInputChange = (event) => {
    const { value } = event.target;
    setInputValue(value);
  };

  const renderNotConnectedContainer = () => (
    <button
      className="cta-button connect-wallet-button"
      onClick={connectWallet}
    >
      Connect to Wallet
    </button>
  );

  const renderConnectedContainer = () => (
    <div className="connected-container">
      <form
        onSubmit={(event) => {
          event.preventDefault();
        }}
      >
        <input type="text" placeholder="Enter gif link!" value={inputValue} onChange={onInputChange}/>
        <button type="submit" className="cta-button submit-gif-button">Submit</button>
      </form>
      <div className="gif-grid">
        {TEST_GIFS.map((gif) => (
          <div className="gif-item" key={gif}>
            <img src={gif} alt={gif} />
          </div>
        ))}
      </div>
    </div>
  );

  /*
   * ÂàùÂõû„ÅÆ„É¨„É≥„ÉÄ„É™„É≥„Ç∞ÊôÇ„Å´„ÅÆ„Åø„ÄÅPhantom Wallet„ÅåÊé•Á∂ö„Åï„Çå„Å¶„ÅÑ„Çã„Åã„Å©„ÅÜ„ÅãÁ¢∫Ë™ç„Åó„Åæ„Åô„ÄÇ
   */
  useEffect(() => {
    const onLoad = async () => {
      await checkIfWalletIsConnected();
    };
    window.addEventListener('load', onLoad);
    return () => window.removeEventListener('load', onLoad);
  }, []);

  return (
    <div className="App">
      <div className="container">
        <div className="header-container">
          <p className="header">üñº GIF Portal</p>
          <p className="sub-text">
            View your GIF collection ‚ú®
          </p>
          {!walletAddress && renderNotConnectedContainer()}
        </div>
        <main className="main">
          {/* „Ç¶„Ç©„É¨„ÉÉ„Éà„ÅåÊé•Á∂ö„Åï„Çå„Å¶„ÅÑ„ÇãÂ†¥Âêà„Å´renderConnectedContainerÈñ¢Êï∞„ÇíÂÆüË°å„Åó„Åæ„Åô„ÄÇ */}
          {walletAddress && renderConnectedContainer()}
        </main>
        <div className="footer-container">
          <img alt="Twitter Logo" className="twitter-logo" src={twitterLogo} />
          <a
            className="footer-text"
            href={TWITTER_LINK}
            target="_blank"
            rel="noreferrer"
          >{`built on @${TWITTER_HANDLE}`}</a>
        </div>
      </div>
    </div>
  );
};

export default App;
