// App.js

import React, { useEffect,useState } from 'react';
import twitterLogo from './assets/twitter-logo.svg';
import './App.css';

// ÂÆöÊï∞„ÇíÂÆ£Ë®Ä„Åó„Åæ„Åô„ÄÇ
const TWITTER_HANDLE = '„ÅÇ„Å™„Åü„ÅÆTwitter„Éè„É≥„Éâ„É´';
const TWITTER_LINK = `https://twitter.com/${TWITTER_HANDLE}`;

const App = () => {

 /*
  * Phantom Wallet„ÅåÊé•Á∂ö„Åï„Çå„Å¶„ÅÑ„Çã„Åã„Å©„ÅÜ„Åã„ÇíÁ¢∫Ë™ç„Åô„Çã„Åü„ÇÅ„ÅÆÈñ¢Êï∞„Åß„Åô„ÄÇ
  */
  const checkIfWalletIsConnected = async () => {
    try {
      const { solana } = window;

      if (solana) {
        if (solana.isPhantom) {
          console.log('Phantom wallet found!');
          const response = await solana.connect({ onlyIfTrusted: true });
          console.log(
            'Connected with Public Key:',
            response.publicKey.toString()
          );
        }
      } else {
        alert('Solana object not found! Get a Phantom Wallet üëª');
      }
    } catch (error) {
      console.error(error);
    }
  };

  /*
   * „ÄåConnect to Wallet„Äç„Éú„Çø„É≥„ÇíÊäº„Åó„Åü„Å®„Åç„Å´Âãï‰Ωú„Åô„ÇãÈñ¢Êï∞„Åß„Åô„ÄÇÔºàÁßªË°å„ÅÆSection„ÅßÂ§âÊõ¥„Åô„Çã„ÅÆ„Åß„ÄÅÁèæÁä∂„ÅØÂÜÖÈÉ®Âá¶ÁêÜ„Åå„Å™„ÅÑ„Åæ„ÅæÈÄ≤„Åø„Åæ„Åô„ÄÇÔºâ
   */
  const connectWallet = async () => {};

  /*
   * „É¶„Éº„Ç∂„Éº„ÅåWeb„Ç¢„Éó„É™„Ç±„Éº„Ç∑„Éß„É≥„Çí„Ç¶„Ç©„É¨„ÉÉ„Éà„Å´Êé•Á∂ö„Åó„Å¶„ÅÑ„Å™„ÅÑ„Å®„Åç„Å´Ë°®Á§∫„Åô„ÇãUI„Åß„Åô„ÄÇ
   */
  const renderNotConnectedContainer = () => (
    <button
      className="cta-button connect-wallet-button"
      onClick={connectWallet}
    >
      Connect to Wallet
    </button>
  );

  /*
   * ÂàùÂõû„ÅÆ„É¨„É≥„ÉÄ„É™„É≥„Ç∞ÊôÇ„Å´„ÅÆ„Åø„ÄÅPhantom Wallet„ÅåÊé•Á∂ö„Åï„Çå„Å¶„ÅÑ„Çã„Åã„Å©„ÅÜ„ÅãÁ¢∫Ë™ç„Åó„Åæ„Åô„ÄÇ
   */
  useEffect(() => {
    const onLoad = async () => {
      await checkIfWalletIsConnected();
    };
    window.addEventListener('load', onLoad);
    return () => window.removeEventListener('load', onLoad);
  }, []);

  return (
    <div className="App">
      <div className="container">
        <div className="header-container">
          <p className="header">üñº GIF Portal</p>
          <p className="sub-text">
            View your GIF collection ‚ú®
          </p>
          {/* „Åì„Åì„Åß„Ç¶„Ç©„É¨„ÉÉ„Éà„Å∏„ÅÆÊé•Á∂ö„Éú„Çø„É≥„Çí„É¨„É≥„ÉÄ„É™„É≥„Ç∞„Åó„Åæ„Åô„ÄÇ */}
          {renderNotConnectedContainer()}
        </div>
        <div className="footer-container">
          <img alt="Twitter Logo" className="twitter-logo" src={twitterLogo} />
          <a
            className="footer-text"
            href={TWITTER_LINK}
            target="_blank"
            rel="noreferrer"
          >{`built on @${TWITTER_HANDLE}`}</a>
        </div>
      </div>
    </div>
  );
};

export default App;
